var vm;window.onload=function(){vm=new Vue({el:"#projects",data:{modalActive:!1,activeModalContent:{title:""},projectPreviews:[{title:"What's WR?",subtitle:"Find a random speedrun world record to watch!"}]},methods:{activateModal:function(t){this.toggleModal(),this.setActiveModalContent(t)},deactivateModal:function(t){"modal-exit-button"!==t.target.id&&"modal"!==t.target.id||this.toggleModal()},toggleModal:function(){this.modalActive=!this.modalActive},setActiveModalContent:function(t){this.activeModalContent=this.projectPreviews[t]}}})};